query:
CREATE TABLE IF NOT EXISTS early_access_users (
  id SERIAL PRIMARY KEY,
  full_name TEXT NOT NULL,
  email TEXT NOT NULL UNIQUE,
  phone TEXT,
  username TEXT NOT NULL UNIQUE,
  source TEXT NOT NULL,
  referral_code TEXT NOT NULL UNIQUE,
  referred_by TEXT,
  referral_count INTEGER DEFAULT 0 NOT NULL,
  bonus_years INTEGER DEFAULT 1 NOT NULL,
  created_at TIMESTAMP DEFAULT NOW() NOT NULL
);

CREATE UNIQUE INDEX IF NOT EXISTS early_access_email_idx ON early_access_users(email);
CREATE UNIQUE INDEX IF NOT EXISTS early_access_username_idx ON early_access_users(username);
CREATE UNIQUE INDEX IF NOT EXISTS early_access_referral_code_idx ON early_access_users(referral_code);
CREATE INDEX IF NOT EXISTS early_access_referred_by_idx ON early_access_users(referred_by);

